import React, { useEffect, useState } from 'react';
import './index.css';
import TimePicker from 'react-time-picker';
import 'react-time-picker/dist/TimePicker.css';
import 'react-clock/dist/Clock.css';

function CustomerPage() {
  const [customers, setCustomers] = useState([]);
  const [name, setName] = useState('');
  const [appointment, setAppointment] = useState('');
  const [barber, setBarber] = useState('');
  const [error, setError] = useState('');

  useEffect(() => {
    fetchCustomers();
  }, []);

  const fetchCustomers = () => {
    fetch('http://localhost:5000/api/customers')
      .then(response => response.json())
      .then(data => setCustomers(data))
      .catch(error => console.error('Error fetching customers:', error));
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    const newCustomer = { name, appointment, barber };

    fetch('http://localhost:5000/api/customers', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(newCustomer)
    })
      .then(async (res) => {
        if (!res.ok) {
          const t = await res.json();
          throw new Error(t.message || "Booking Failed");
        }
        fetchCustomers();
        setName('');
        setAppointment('');
        setBarber('');
        setError('');
      })
      .catch(err => {
        console.error('Error:', err);
        setError(err.message);
      });
  };

  const handleDelete = (id) => {
    fetch(`http://localhost:5000/api/customers/${id}`, {
      method: 'DELETE'
    })
      .then(() => fetchCustomers())
      .catch(err => console.error('Error deleting customer:', err));
  };

  const grouped = customers.reduce((acc, curr) => {
    acc[curr.barber] = acc[curr.barber] || [];
    acc[curr.barber].push(curr);
    return acc;
  }, {});

  return (
    <div className="p-6 max-w-4xl mx-auto">
      <h1 className="text-3xl font-bold mb-6 text-gray-800">Customer Appointment Booking</h1>

      {error && (
        <p className="text-red-600 mb-4">{error}</p>
      )}

      <form
        onSubmit={handleSubmit}
        className="bg-white shadow-md rounded-lg p-6 flex flex-wrap items-center gap-4 mb-8"
      >
        <div className="flex flex-col w-[200px]">
          <label className="text-sm text-gray-600 mb-1">Your Name</label>
          <input
            type="text"
            value={name}
            required
            onChange={(e) => setName(e.target.value)}
            className="border rounded-md px-3 py-2"
            placeholder="Enter name"
          />
        </div>

        <div className="flex flex-col">
          <label className="text-sm text-gray-600 mb-1">Appointment Time</label>
          <TimePicker
            onChange={setAppointment}
            value={appointment}
            disableClock={true}
            required
          />
        </div>

        <div className="flex flex-col w-[200px]">
          <label className="text-sm text-gray-600 mb-1">Select Barber</label>
          <select
            value={barber}
            onChange={(e) => setBarber(e.target.value)}
            required
            className="border rounded-md px-3 py-2"
          >
            <option value="">--Choose Barber--</option>
            <option value="Raj">Raj</option>
            <option value="Aman">Aman</option>
            <option value="Sana">Sana</option>
          </select>
        </div>

        <button
          type="submit"
          className="bg-blue-600 text-white px-6 py-2 rounded-md hover:bg-blue-700"
        >
          Book
        </button>
      </form>

      <h2 className="text-2xl font-semibold mb-4 text-gray-800">Existing Appointments</h2>

      {Object.keys(grouped).map((barberName) => (
        <div key={barberName} className="bg-gray-100 shadow-sm rounded-md p-4 mb-6">
          <h3 className="text-xl font-semibold text-gray-700 mb-2">{barberName}'s Queue</h3>
          <hr className="mb-3 border-gray-300" />
          <ul className="space-y-2">
            {grouped[barberName].map((customer) => (
              <li
                key={customer._id}
                className="flex justify-between items-center bg-white px-4 py-2 rounded shadow-sm"
              >
                <span className="text-gray-800">{customer.name} – {customer.appointment}</span>
                <button
                  onClick={() => handleDelete(customer._id)}
                  className="text-green-600 hover:text-green-800 font-semibold"
                >
                  ✔ Done
                </button>
              </li>
            ))}
          </ul>
        </div>
      ))}
    </div>
  );
}

export default CustomerPage;
