import React, { useState } from 'react';
import { Box, Grid, TextField, Button, MenuItem, Typography, Paper, IconButton } from '@mui/material';
import { TimePicker } from '@mui/x-date-pickers/TimePicker';
import { AdapterDateFns } from '@mui/x-date-pickers/AdapterDateFns';
import { LocalizationProvider } from '@mui/x-date-pickers/LocalizationProvider';
import DeleteIcon from '@mui/icons-material/Delete';

const CustomerPage = () => {
  const [name, setName] = useState('');
  const [time, setTime] = useState(null);
  const [selectedBarber, setSelectedBarber] = useState('');
  const [appointments, setAppointments] = useState([]);

  const barbers = ['Aman', 'Rahul', 'Dev'];

  const handleBook = () => {
    if (name && time && selectedBarber) {
      const formattedTime = time.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
      const newAppointment = { name, time: formattedTime, barber: selectedBarber };
      setAppointments([...appointments, newAppointment]);
      setName('');
      setTime(null);
      setSelectedBarber('');
    }
  };

  const handleDelete = (index) => {
    setAppointments(appointments.filter((_, i) => i !== index));
  };

  return (
    <LocalizationProvider dateAdapter={AdapterDateFns}>
      <Box sx={{ maxWidth: 800, mx: 'auto', mt: 5, p: 3, boxShadow: 3, borderRadius: 2, backgroundColor: '#fff' }}>
        <Typography variant="h4" gutterBottom>
          Book Your Appointment
        </Typography>

        <Grid container spacing={2}>
          <Grid item xs={12} sm={4}>
            <TextField
              fullWidth
              label="Your Name"
              value={name}
              onChange={(e) => setName(e.target.value)}
              required
            />
          </Grid>

          <Grid item xs={12} sm={4}>
            <TimePicker
              label="Select Time"
              value={time}
              onChange={(newValue) => setTime(newValue)}
              renderInput={(params) => <TextField fullWidth {...params} />}
            />
          </Grid>

          <Grid item xs={12} sm={4}>
            <TextField
              select
              fullWidth
              label="Choose Barber"
              value={selectedBarber}
              onChange={(e) => setSelectedBarber(e.target.value)}
              required
            >
              {barbers.map((barber, i) => (
                <MenuItem key={i} value={barber}>
                  {barber}
                </MenuItem>
              ))}
            </TextField>
          </Grid>
        </Grid>

        <Button
          variant="contained"
          fullWidth
          sx={{ mt: 3 }}
          onClick={handleBook}
        >
          BOOK APPOINTMENT
        </Button>

        <Box mt={5}>
          <Typography variant="h5" gutterBottom>
            Queues
          </Typography>
          {appointments.map((appt, index) => (
            <Paper key={index} sx={{ p: 2, mb: 2, display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
              <Box>
                <Typography variant="subtitle1" fontWeight="bold">
                  {appt.barber}'s Queue
                </Typography>
                <Typography variant="body1">
                  {appt.name} – {appt.time}
                </Typography>
              </Box>
              <IconButton color="error" onClick={() => handleDelete(index)}>
                <DeleteIcon />
              </IconButton>
            </Paper>
          ))}
        </Box>
      </Box>
    </LocalizationProvider>
  );
};

export default CustomerPage;
