import React, { useEffect, useState } from 'react';

function CustomerPage() {
  const [customers, setCustomers] = useState([]);
  const [name, setName] = useState('');
  const [appointment, setAppointment] = useState('');
  const [barber, setBarber] = useState('');


  useEffect(() => {
    fetchCustomers();
  }, []);


  const fetchCustomers = () => {
    fetch('http://localhost:5000/api/customers')
      .then(response => response.json())
      .then(data => setCustomers(data))
      .catch(error => console.error('Error fetching customers:', error));
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    const newCustomer = { name, appointment ,barber };

    fetch('http://localhost:5000/api/customers', {
      method: 'POST',
      headers: {
    'Content-Type': 'application/json'
  },
      body: JSON.stringify(newCustomer)
    })
      .then(() => {
        fetchCustomers(); 
        setName('');
        setAppointment('');
      })
      .catch(err => console.error('Error:', err));
  };

  const handleDelete = (id) => {
  fetch(`http://localhost:5000/api/customers/${id}`, {
    method: 'DELETE'
  })
    .then(() => fetchCustomers()) // Refresh queue
    .catch(err => console.error('Error deleting customer:', err));
};

  const grouped = customers.reduce((acc, curr) => {
  acc[curr.barber] = acc[curr.barber] || [];
  acc[curr.barber].push(curr);
  return acc;
}, {});


  return (
    <div style={{ padding: '20px' }}>
      <h1>Customer Appointment Booking</h1>

      <form onSubmit={handleSubmit} style={{ marginBottom: '20px' }}>
        <input
          type="text"
          placeholder="Your Name"
          value={name}
          required
          onChange={(e) => setName(e.target.value)}
          style={{ padding: '8px', marginRight: '10px' }}
        />
        <input
          type="time"
          value={appointment}
          required
          onChange={(e) => setAppointment(e.target.value)}
          style={{ padding: '8px', marginRight: '10px' }}
        />
        <select
          value={barber}
          required
          onChange={(e) => setBarber(e.target.value)}
          style={{ padding: '8px', marginRight: '10px' }}
          >
          <option value="">Select Barber</option>
          <option value="Raj">Raj</option>
          <option value="Aman">Aman</option>
          <option value="Sana">Sana</option>
        </select>

        <button type="submit" style={{ padding: '8px 16px' }}>Book</button>
      </form>

      <h2>Existing Appointments</h2>
      {Object.keys(grouped).map((barberName) => (
        <div key={barberName}>
        <h3>{barberName}'s Queue</h3>
    <ul>
      {grouped[barberName].map((customer) => (
        <li key={customer.id}>
          {customer.name} â€“ {customer.appointment}
           <button
    onClick={() => handleDelete(customer._id)}
    style={{ marginLeft: '10px', padding: '4px 8px' }}
  >
    Done
  </button>
        </li>
      ))}
    </ul>
  </div>
))}

    </div>
  );
}

export default CustomerPage;
