import React, { useEffect, useState } from 'react';
import TimePicker from 'react-time-picker';
import 'react-time-picker/dist/TimePicker.css';
import 'react-clock/dist/Clock.css';

function CustomerPage() {
  const [customers, setCustomers] = useState([]);
  const [name, setName] = useState('');
  const [appointment, setAppointment] = useState('');
  const [barber, setBarber] = useState('');

  useEffect(() => {
    fetchCustomers();
  }, []);

  const fetchCustomers = () => {
    fetch('http://localhost:5000/api/customers')
      .then(response => response.json())
      .then(data => setCustomers(data))
      .catch(error => console.error('Error fetching customers:', error));
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    const newCustomer = { name, appointment, barber };

    fetch('http://localhost:5000/api/customers', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(newCustomer)
    })
      .then(() => {
        fetchCustomers();
        setName('');
        setAppointment('');
      })
      .catch(err => console.error('Error:', err));
  };

  const handleDelete = (id) => {
    fetch(`http://localhost:5000/api/customers/${id}`, {
      method: 'DELETE'
    })
      .then(() => fetchCustomers())
      .catch(err => console.error('Error deleting customer:', err));
  };

  const grouped = customers.reduce((acc, curr) => {
    acc[curr.barber] = acc[curr.barber] || [];
    acc[curr.barber].push(curr);
    return acc;
  }, {});

  return (
    <div style={{ padding: '20px' }}>
      <h1 style={{ marginBottom: '20px' }}>Customer Appointment Booking</h1>

      <form onSubmit={handleSubmit} style={{ display: 'flex', gap: '10px', marginBottom: '30px', alignItems: 'center' }}>
        <input
          type="text"
          placeholder="Your Name"
          value={name}
          required
          onChange={(e) => setName(e.target.value)}
          style={{ padding: '8px', width: '150px' }}
        />

        <TimePicker
          onChange={setAppointment}
          value={appointment}
          disableClock={true}
          required
        />

        <select
          value={barber}
          required
          onChange={(e) => setBarber(e.target.value)}
          style={{ padding: '8px', width: '150px' }}
        >
          <option value="">Select Barber</option>
          <option value="Raj">Raj</option>
          <option value="Aman">Aman</option>
          <option value="Sana">Sana</option>
        </select>

        <button type="submit" style={{ padding: '8px 16px' }}>Book</button>
      </form>

      <h2>Existing Appointments</h2>
      {Object.keys(grouped).map((barberName) => (
        <div key={barberName} style={{ marginBottom: '20px' }}>
          <h3>{barberName}'s Queue</h3>
          <ul>
            {grouped[barberName].map((customer) => (
              <li key={customer.id} style={{ marginBottom: '5px' }}>
                {customer.name} â€“ {customer.appointment}
                <button
                  onClick={() => handleDelete(customer.id)}
                  style={{ marginLeft: '10px', padding: '4px 8px' }}
                >
                  Done
                </button>
              </li>
            ))}
          </ul>
        </div>
      ))}
    </div>
  );
}

export default CustomerPage;
